import { observable, decorate } from "mobx";

class PongStore {
  width = 20;
  get vel() {
    return (this.width * 3) / 4;
  }
  keyboardmode = true;
  ballxvel = 15;
  ballyvel = 15;
  firstscore = 0;
  secondscore = 0;
  gameover = false;
}
decorate(PongStore, {
  keyboardmode: observable,
  ballxvel: observable,
  ballyvel: observable
});

export default new PongStore();
