import styled from "styled-components";

const px = l => l + "px";

let Width = 20;

const Container = styled.div`
  .game {
    width: 100%;
    height: 100vh;
    background-color: black;
    position: relative;

    .middash {
      position: absolute;
      height: 100vh;
      width: ${px(middashWidth)};
      background-image: linear-gradient(
        to bottom,
        #fff 60%,
        rgba(0, 0, 0, 0) 0%
      );
      background-position: left;
      background-size: ${px(middashWidth)} ${px(13 * middashWidth)};
      background-repeat: repeat-y;
      left: 50%;
      margin-left: ${px(-middashWidth / 2)};
    }
    .bars {
      width: ${px(barsWidth)};
      height: ${px(barsWidth * 6)};
      background-color: white;
      position: absolute;
      margin: ${px(-barsWidth * 3)} ${px(barsWidth * 2)} 0;
      top: 50vh;
      &:nth-child(2) {
        right: 0;
      }
    }
  }
`;

export default Container;
