import React, { Component } from "react";
import { observer } from "mobx-react";
import Container from "./style";
import "../../fontawesome/css/all.min.css";

const Timer = observer(
  class Timer extends Component {
    componentDidMount() {
      this.refs.seconds.innerHTML = "0";
    }
    handleKeyPress = e => {
      e.preventDefault();
      if (Number.parseInt(e.key)) {
        if (this.props.store.seconds) {
          e.target.innerHTML = e.key;
        } else {
          e.target.innerHTML += e.key;
        }
      } else {
        e.target.innerHTML = previousNum;
      }
    };
    render() {
      return (
        <Container>
          <div className="timer_outer">
            <div className="timer_inner">
              <div className="display">
                <div className="time">00:00:00:00</div>
                <div
                  className="seconds"
                  onKeyDown={this.handleKeyPress}
                  onFocus={() =>
                    (this.refs.seconds.innerHTML = this.refs.seconds.innerHTML)
                  }
                  contentEditable
                  ref="seconds"
                />
              </div>
              <div className="buttons">
                <button
                  className="play"
                  disabled={this.props.store.seconds === 0}
                >
                  <i className="fas fa-play" />
                </button>
                <button className="reset">
                  <i className="fas fa-sync-alt" />
                </button>
              </div>
            </div>
          </div>
        </Container>
      );
    }
  }
);

export default Timer;
