import styled from "styled-components";

const px = l => l + "px";

let middashWidth = 4;

const Container = styled.div`
  .game {
    width: 100%;
    height: 100vh;
    background-color: black;
    position: relative;

    .middash {
      position: absolute;
      height: 100vh;
      width: ${px(middashWidth)};
      background-image: linear-gradient(
        to bottom,
        #fff 60%,
        rgba(255, 255, 255, 0) 0%
      );
      background-position: left;
      background-size: ${px(middashWidth)} ${px(13 * middashWidth)};
      background-repeat: repeat-y;
      left: 50%;
      margin-left: ${-middashWidth / 2 + "px"};
    }
    .left.dotted-spaced {
      background-image: linear-gradient(
        to bottom,
        #333 10%,
        rgba(255, 255, 255, 0) 0%
      );
      background-position: left;
      background-size: 1px 10px;
      background-repeat: repeat-y;
    }
  }
`;

export default Container;
