import React, { Component } from "react";
import { observer } from "mobx-react";
import Container from "./styles/style";

const Pong = observer(
  class Pong extends Component {
    componentDidMount() {
      document.addEventListener("keydown", this.handleKeyPress);
    }
    handleKeyPress = e => {
      switch (e.key) {
        case "a":
          FirstBarUp();
          break;
        case "z":
          FirstBarDown();
          break;
        case "k":
          SecondBarUp();
          break;
        case "m":
          SecondBarDown();
          break;
        default:
          break;
      }
    };
    render() {
      let { firsttop, secondtop } = this.props.store;
      return (
        <Container firsttop={firsttop} secondtop={secondtop}>
          <div className="game">
            <div className="middash" />
            <div className="bars" />
            <div className="bars" />
            <div className="ball" />
          </div>
        </Container>
      );
    }
  }
);

export default Pong;
