import styled from "styled-components";

let middashWidth = 2;

const Container = styled.div`
  .game {
    width: 100%;
    height: 100vh;
    background-color: black;
    position: relative;

    .middash {
      position: absolute;
      height: 100vh;
      border: 2px dashed white;
      left: 50%;
      margin-left: -2px;
    }
  }
`;

export default Container;
