import { observable, decorate, computed } from "mobx";

class TimerStore {
  seconds = 0;
  start = false;
  get time() {
    let s = this.seconds;
    let h = Math.floor(s / 60 / 60);
    let m = s - h * 60 * 60;
  }
}
decorate(TimerStore, {
  seconds: observable,
  start: observable,
  time: computed
});

export default new TimerStore();
