import { observer } from "mobx-react";
import Container from "./styles/style";
import kd from "./keydrown";

const Pong = observer(
  class Pong extends Component {
    componentDidMount() {
      this.refs.firstbar.style.top = window.innerHeight / 2 + "px";
      this.refs.secondbar.style.top = window.innerHeight / 2 + "px";
      kd.A.down(function() {
        console.log('The "A" key is being held down!');
      });
      kd.K.down(function() {
        console.log('The "K" key is being held down!');
      });
      kd.run(function() {
        kd.tick();
      });
      //   document.addEventListener("keydown", this.handleKeyDownPress);
      //   document.addEventListener("keyup", this.handleKeyUpPress);
