import React, { Component } from "react";
import { observer } from "mobx-react";
import Container from "./styles/style";

const Pong = observer(
  class Pong extends Component {
    componentDidMount() {
      this.refs.firstbar.style.top = window.innerHeight / 2 + "px";
      this.refs.secondbar.style.top = window.innerHeight / 2 + "px";
      document.addEventListener("keydown", this.handleKeyPress);
      document.addEventListener("keyup", this.handleKeyPress);
    }
    handleKeyPress = e => {
      let map = this.props.store.map;
      map[e.key] = e.type === "keydown";

      let Width = this.refs.firstbar.offsetWidth;
      console.log(Width);

      let firsttop = Number.parseInt(
        this.refs.firstbar.style.top.split("px")[0]
      );
      let secondtop = Number.parseInt(
        this.refs.secondbar.style.top.split("px")[0]
      );

      if (map.a) {
        this.refs.firstbar.style.top = firsttop - 1 + "px";
      }
      if (map.z) {
        this.refs.firstbar.style.top = firsttop + 1 + "px";
      }
      if (map.k) {
        this.refs.secondbar.style.top = secondtop - 1 + "px";
      }
      if (map.m) {
        this.refs.secondbar.style.top = secondtop + 1 + "px";
      }
    };
    render() {
      return (
        <Container>
          <div className="game">
            <div className="middash" />
            <div className="bars" ref="firstbar" />
            <div className="bars" ref="secondbar" />
            <div className="ball" ref="bal" />
          </div>
        </Container>
      );
    }
  }
);

export default Pong;
