import React, { Component } from "react";
import { observer } from "mobx-react";
import Container from "./styles/style";
import kd from "./keydrown";

const Pong = observer(
  class Pong extends Component {
    componentDidMount() {
      this.refs.firstbar.style.top = window.innerHeight / 2 + "px";
      this.refs.secondbar.style.top = window.innerHeight / 2 + "px";

      kd.A.down(function() {
        this.firstBarUp();
      });
      kd.Z.down(function() {
        this.firstBarDown();
      });
      kd.K.down(function() {
        this.secondBarUp();
      });
      kd.M.down(function() {
        this.secondBarDown();
      });

      kd.run(function() {
        kd.tick();
      });
    }
    GetVals = () => {
      return {
        Width: this.props.store.width,
        vel: this.props.store.vel,
        firsttop: Number.parseInt(this.refs.firstbar.style.top.split("px")[0]),
        secondtop: Number.parseInt(this.refs.secondbar.style.top.split("px")[0])
      };
    };
    firstBarUp = () => {
      let { Width, vel, firsttop } = this.GetVals();
      if (firsttop - Width * 3 > 0) {
        this.refs.firstbar.style.top = firsttop - vel + "px";
      }
    };
    firstBarDown = () => {
      let { Width, vel, firsttop } = this.GetVals();
      if (firsttop + Width * 3 < window.innerHeight - 4) {
        this.refs.firstbar.style.top = firsttop + vel + "px";
      }
    };
    secondBarUp = () => {
      let { Width, vel, secondtop } = this.GetVals();
      if (secondtop - Width * 3 > 0) {
        this.refs.secondbar.style.top = secondtop - vel + "px";
      }
    };
    secondBarDown = () => {
      let { Width, vel, secondtop } = this.GetVals();
      if (secondtop + Width * 3 < window.innerHeight - 4) {
        this.refs.secondbar.style.top = secondtop + vel + "px";
      }
    };
    handleKeyDownPress = e => {
      let map = this.props.store.map;
      map[e.key] = e.type === "keydown";

      //   let Width = this.props.store.width;
      //   let vel = this.props.store.vel;
      //   let firsttop = Number.parseInt(
      //     this.refs.firstbar.style.top.split("px")[0]
      //   );
      //   let secondtop = Number.parseInt(
      //     this.refs.secondbar.style.top.split("px")[0]
      //   );

      //   if (map.a) {
      //     if (firsttop - Width * 3 > 0) {
      //       this.refs.firstbar.style.top = firsttop - vel + "px";
      //     }
      //   }
      //   if (map.z) {
      //     if (firsttop + Width * 3 < window.innerHeight - 4) {
      //       this.refs.firstbar.style.top = firsttop + vel + "px";
      //     }
      //   }
      //   if (map.k) {
      //     if (secondtop - Width * 3 > 0) {
      //       this.refs.secondbar.style.top = secondtop - vel + "px";
      //     }
      //   }
      //   if (map.m) {
      //     if (secondtop + Width * 3 < window.innerHeight - 4) {
      //       this.refs.secondbar.style.top = secondtop + vel + "px";
      //     }
      //   }
    };
    render() {
      return (
        <Container>
          <div className="game">
            <div className="middash" />
            <div className="bars" ref="firstbar" />
            <div className="bars" ref="secondbar" />
            <div className="ball" />
          </div>
        </Container>
      );
    }
  }
);

export default Pong;
