import React, { Component } from "react";
import { observer } from "mobx-react";
import Container from "./style";
import "../../fontawesome/css/all.min.css";

const Timer = observer(
  class Timer extends Component {
    componentDidMount() {
      this.refs.seconds.innerHTML = this.props.store.seconds;
    }
    handleKeyPress = e => {
      console.log(99 * 60 * 60 + 59 * 60 + 59);
      let maxSeconds = 359999;
      if (!Number.isInteger(Number.parseInt(e.key)) && e.key !== "Backspace") {
        e.preventDefault();
      } else if (e.key === "Backspace" && e.target.innerHTML.length === 1) {
        e.target.innerHTML = "0";
      } else if (e.target.innerHTML === "0") {
        e.target.innerHTML = "";
      } else if (Number.parseInt(e.target.innerHTML + e.key) > 10) {
        e.preventDefault();
      }
      //   e.preventDefault();
      //   if (Number.isInteger(Number.parseInt(e.key))) {
      //     this.props.store.seconds *= 10;
      //     this.props.store.seconds += Number.parseInt(e.key);
      //   } else if (e.key === "Backspace") {
      //     this.props.store.seconds /= 10;
      //     this.props.store.seconds = Math.floor(this.props.store.seconds);
      //   }
      //   e.target.innerHTML = this.props.store.seconds;
    };
    render() {
      console.log();
      return (
        <Container>
          <div className="timer_outer">
            <div className="timer_inner">
              <div className="display">
                <div className="time">00:00:00:00</div>
                <div
                  className="seconds"
                  onKeyDown={this.handleKeyPress}
                  onFocus={() =>
                    (this.refs.seconds.innerHTML = this.refs.seconds.innerHTML)
                  }
                  contentEditable
                  ref="seconds"
                />
              </div>
              <div className="buttons">
                <button
                  className="play"
                  disabled={this.props.store.seconds === 0}
                >
                  <i className="fas fa-play" />
                </button>
                <button className="reset">
                  <i className="fas fa-sync-alt" />
                </button>
              </div>
            </div>
          </div>
        </Container>
      );
    }
  }
);

export default Timer;
