import React, { Component } from "react";

class Operations extends Component {
  state = {
    operations: ["+", "−", "×", "÷"]
  };
  render() {
    return (
      <React.Fragment>
        {this.state.operations.map(oper, index => (
          <button
            className={"oper" + index}
            key={oper}
            onClick={() => this.props.onOperPress(oper)}
          >
            {oper}
          </button>
        ))}
      </React.Fragment>
    );
  }
}

export default Operations;
