import React, { Component } from "react";
import Display from "./components/display";
import Calculator from "./components/calculator";

class App extends Component {
  state = {
    number: 0,
    result: 0,
    clear: "AC",
    decimal: false,
    operation: false
  };
  handleNumPress = num => {
    if (this.state.operation) {
      this.setState({ operation: false, number: num });
    } else {
      let number = this.state.number;
      number *= 10;
      number += num;
      if (number !== this.state.number) {
        this.setState({ number, result: number, clear: "C" });
      }
    }
  };
  handleNumModPress = mod => {
    let number = this.state.number;
    switch (mod) {
      case "AC":
        number = 0;
        this.setState({
          clear: "AC",
          decimal: false,
          operation: false
        });
        break;
      case "%":
        number /= 100;
        break;
      case "+/-":
        number *= -1;
        break;
      default:
        break;
    }
    this.setState({ number, result: number });
  };
  handleOperPress = oper => {
    this.setState(state => ({
      operation: true,
      typeOper: oper,
      prevNumber: state.number
    }));
  };
  operate = () => {
    let { number, prevNumber, typeOper } = this.state;
    let result;
    switch (typeOper) {
      case "+":
        result = prevNumber + number;
        break;
      case "−":
        if (this.state.operation) {
          result = number - prevNumber;
        } else {
          result = prevNumber - number;
        }
        break;
      case "×":
        result = prevNumber * number;
        break;
      case "÷":
        if (this.state.operation) {
          result = number / prevNumber;
        } else {
          result = prevNumber / number;
        }
        break;
      default:
        break;
    }
    return result;
  };
  handleEqualsPress = () => {
    let result = this.operate();
    if (result !== undefined) {
      this.setState({ number: result });
    }
  };
  render() {
    return (
      <div className="App">
        <Display number={this.state.number} />
        <Calculator
          clear={this.state.clear}
          onNumPress={this.handleNumPress}
          onNumModPress={this.handleNumModPress}
          onOperPress={this.handleOperPress}
          onEqualsPress={this.handleEqualsPress}
        />
      </div>
    );
  }
}

export default App;
