import React, { Component } from "react";
import Display from "./components/display";
import Calculator from "./components/calculator";

class App extends Component {
  state = {
    number: 0,
    clear: "AC",
    decimal: false,
    operation: false
  };
  handleNumPress = num => {
    if (this.state.operation) {
      this.setState({ operation: false });
      let number = 0;
      number += num;
      this.setState({ number });
    } else {
      let number = this.state.number;
      number *= 10;
      number += num;
      if (number !== this.state.number) {
        this.setState({ number, clear: "C" });
      }
    }
  };
  handleNumModPress = mod => {
    let number = this.state.number;
    switch (mod) {
      case "AC":
        number = 0;
        this.setState({ clear: "AC", decimal: false, operation: false });
        break;
      case "%":
        number /= 100;
        break;
      case "+/-":
        number *= -1;
        break;
      default:
        break;
    }
    this.setState({ number });
  };
  handleOperPress = oper => {
    this.setState({
      operation: true,
      typeOper: oper,
      prevNumber: this.state.number
    });
    this.operate();
  };
  operate = () => {
    let { number, prevNumber, typeOper } = this.state;
    switch (typeOper) {
      case "+":
        break;
      case "−":
        break;
      case "×":
        break;
      case "÷":
        break;
      default:
        break;
    }
  };
  render() {
    return (
      <div className="App">
        <Display number={this.state.number} />
        <Calculator
          clear={this.state.clear}
          onNumPress={this.handleNumPress}
          onNumModPress={this.handleNumModPress}
          onOperPress={this.handleOperPress}
        />
      </div>
    );
  }
}

export default App;
