import React, { Component } from "react";

class Counter extends Component {
  state = {
    count: 0,
    randimgurl: "http://picsum.photos/300",
    tags: ["tag1", "tag2", "tag3", "tag4", "tag5"]
  };
  styles = {
    color: "green",
    fontSize: 20,
    backgroundColor: "yellow"
  };
  renderTags() {
    if (this.state.tags.length === 0) return <p>There are no tags!</p>;
    return (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }
  handleIncrement() {
    console.log("Incremented", countSelf);
  }
  render() {
    const countSelf = this;
    return (
      <React.Fragment>
        {/* <span>{this.state.count}</span>
        <span>{6 * 7}</span>
        <img src={this.state.randimgurl} alt="pic" />
        <span style={this.styles} className="badge badge-primary m-2">
          {this.formatCount()}
        </span>
        <button style={{ fontSize: 30 }} className="btn btn-secondary btn-sm">
          Increment
        </button> */}
        <span className={this.getCounterClasses()}>{this.formatCount()}</span>
        <button
          onClick={this.handleIncrement}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        {this.state.tags.length === 0 && <p>Please put some tags!</p>}
        {this.renderTags()}
      </React.Fragment>
    );
  }
  getCounterClasses() {
    let counterClass = "badge m-2 badge-";
    counterClass += this.state.count === 0 ? "warning" : "primary";
    return counterClass;
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default Counter;
