import React, { Component } from "react";

class Counter extends Component {
  render() {
    const { onIncrement, onDelete, counters } = this.props;
    const { id, value } = counters;
    return (
      <div>
        <span className={this.getBadgeClasses()}>{this.formatCount()}</span>
        <button
          onClick={() => onIncrement(this.props.counter)}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
        <button
          onClick={() => onDelete(this.props.counter.id)}
          className="btn btn-danger btn-sm m-2"
        >
          Delete
        </button>
      </div>
    );
  }
  getBadgeClasses() {
    let counterClass = "badge m-2 badge-";
    counterClass += this.props.counter.value === 0 ? "warning" : "primary";
    return counterClass;
  }

  formatCount() {
    const { value } = this.props.counter;
    return value === 0 ? "Zero" : value;
  }
}

export default Counter;
