import React, { Component } from "react";

class Counter extends Component {
  state = {};
  getBadgeColor() {
    let badgeClass = "badge badge-";
    badgeClass += this.props.counter.value === 0 ? "warning" : "primary";
    return badgeClass;
  }
  render() {
    return (
      <div>
        <span className={this.getBadgeColor()}>{this.props.counter.value}</span>
        <button className="btn btn-secondary btn-md m-1">+</button>
        <button className="btn btn-secondary btn-md m-1">âˆ’</button>
        <button className="btn btn-danger btn-md m-1">Delete</button>
      </div>
    );
  }
}

export default Counter;
