import React, { Component } from "react";
import CartButton from "./subcomponents/cartbutton";
import Cart from "./subcomponents/cart";

class CartPortion extends Component {
  render() {
    let items = this.props.items;
    let numItemsatc = items.reduce((total, item) => {
      return total + item.quantity;
    });
    return (
      <div className="cartportion">
        <CartButton numItemsatc={numItemsatc} />
        <Cart />
      </div>
    );
  }
}

export default CartPortion;
