import React, { Component } from "react";
import Items from "./components/items";
import CartPortion from "./components/cartportion";
import Sizes from "./components/sizes";
import Sorting from "./components/sorting";
import Found from "./components/found";

class App extends Component {
  state = {
    constItems: [
      {
        id: 1,
        freeShipping: true,
        imageurl: "./images/image-1.jpg",
        title: "Cat Tee Black T Shirt",
        price: 10.9,
        size: "S",
        description: "Black with custom print",
        quantity: 0
      }
    ]
  };
  componentWillMount() {
    this.setState({
      items: this.state.constItems
    });
  }
  handleatcClick = id => {
    let items = JSON.parse(JSON.stringify(this.state.items));
    let itemIndex;
    for (let index in items) {
      if (items[index].id === id) {
        itemIndex = index;
        break;
      }
    }
    items[itemIndex].quantity++;
    console.log(items[itemIndex].quantity);
    this.setState({ items });
  };
  handleCheckout = subtotal => {
    if (subtotal !== 0) {
      alert("Checkout - Subtotal: ₹ " + subtotal);
    } else {
      alert("Add some products in the bag");
    }
  };
  render() {
    return (
      <div className="App">
        <Items items={this.state.items} onatcClick={this.handleatcClick} />
        <CartPortion
          items={this.state.items}
          onCheckout={this.handleCheckout}
        />
        <Sizes />
        <Sorting />
        <Found />
      </div>
    );
  }
}

export default App;
