import React, { Component } from "react";
import Items from "./components/items";
import CartPortion from "./components/cartportion";
import Sizes from "./components/sizes";
import Sorting from "./components/sorting";

class App extends Component {
  state = {
    items: [
      {
        id: 1,
        freeShipping: true,
        imageurl: "./images/image-1.jpg",
        title: "Cat Tee Black T Shirt",
        price: 10.9,
        size: "S",
        description: "Black with custom print",
        quantity: 0
      }
    ],
    showCart: false,
    sizes: ["XS", "S", "M", "ML", "L", "XL", "XXL"],
    selectedSizes: []
  };
  componentWillMount() {
    this.setState({
      cartitems: this.state.items
    });
  }
  handleatcClick = id => {
    let cartitems = JSON.parse(JSON.stringify(this.state.cartitems));
    let itemIndex;
    for (let index in cartitems) {
      if (cartitems[index].id === id) {
        itemIndex = index;
        break;
      }
    }
    cartitems[itemIndex].quantity++;
    this.setState({ cartitems });
  };
  handleItemDelete = id => {
    let cartitems = JSON.parse(JSON.stringify(this.state.cartitems));
    let itemIndex;
    for (let index in cartitems) {
      if (cartitems[index].id === id) {
        itemIndex = index;
        break;
      }
    }
    cartitems[itemIndex].quantity = 0;
    this.setState({ cartitems });
  };
  handleCartClick = () => {
    this.setState({ showCart: !this.state.showCart });
  };
  handleCheckout = subtotal => {
    if (subtotal !== 0) {
      alert("Checkout - Subtotal: â‚¹ " + subtotal);
    } else {
      alert("Add some products in the bag");
    }
  };
  handleSizeSelect = index => {
    let selectedSizes = [...this.state.selectedSizes];
    let sizeIndex = selectedSizes.indexOf(this.state.sizes[index]);
    if (sizeIndex === -1) {
      selectedSizes.push(this.state.sizes[index]);
    } else {
      selectedSizes.splice(sizeIndex, 1);
    }
    this.setState({ selectedSizes });
  };
  handleSortSelect = e => {
    let sorting = e.target.value;
    let items = JSON.parse(JSON.stringify(this.state.items));
  };
  render() {
    return (
      <div className="App">
        <Items
          items={this.state.items}
          onatcClick={this.handleatcClick}
          selectedSizes={this.state.selectedSizes}
        />
        <CartPortion
          items={this.state.cartitems.filter(item => item.quantity > 0)}
          showCart={this.state.showCart}
          onItemDelete={this.handleItemDelete}
          onCartClick={this.handleCartClick}
          onCheckout={this.handleCheckout}
        />
        <Sizes sizes={this.state.sizes} onSizeSelect={this.handleSizeSelect} />
        <Sorting onSortSelect={this.handleSortSelect} />
      </div>
    );
  }
}

export default App;
