import React, { Component } from "react";
import Item from "./subcomponents/item";

class Items extends Component {
  render() {
    console.log(this.props.selectedSize);
    let selectedSizes = this.props.selectedSize;
    if (selectedSizes.length === 0) {
      items = this.props.items;
    }
    let items = this.props.items.filter(item =>
      selectedSizes.includes(item.size)
    );
    return (
      <div className="items">
        {items.map(item => (
          <Item key={item.id} item={item} onatcClick={this.props.onatcClick} />
        ))}
      </div>
    );
  }
}

export default Items;
