import React, { Component } from "react";
import CartButton from "./subcomponents/cartportion/cartbutton";
import Cart from "./subcomponents/cartportion/cart";
import CartIcon from "./subcomponents/cartportion/carticon";

class CartPortion extends Component {
  state = {
    showCart: false,
    items: this.props.items.filter(item => item.quantity > 0)
  };
  handleCartClick = () => {
    this.setState({ showCart: !this.state.showCart });
  };
  handleItemDelete = id => {
    let items = JSON.parse(JSON.stringify(this.state.items));
    let index = items.indexOf(items.filter(item => item.id === id));
    items.splice(index, 1);
    this.setState({ items });
  };
  render() {
    let items = this.state.items;
    let numItemsatc = 0;
    let subtotal = 0;
    for (let index in items) {
      numItemsatc += items[index].quantity;
      subtotal += items[index].quantity * items[index].price;
    }
    console.log(items);
    return (
      <div className="cartportion">
        <CartButton
          showCart={this.state.showCart}
          onCartClick={this.handleCartClick}
        >
          <CartIcon numItemsatc={numItemsatc} />
        </CartButton>
        <Cart
          items={items}
          subtotal={subtotal}
          showCart={this.state.showCart}
          onItemDelete={this.handleItemDelete}
          onCheckout={this.props.onCheckout}
        >
          <CartIcon numItemsatc={numItemsatc} />
        </Cart>
      </div>
    );
  }
}

export default CartPortion;
