import React, { Component } from "react";
import Box from "./box";

class Game extends Component {
  state = {
    boxes: [
      { id: 1, value: "", clicked: false },
      { id: 2, value: "", clicked: false },
      { id: 3, value: "", clicked: false },
      { id: 4, value: "", clicked: false },
      { id: 5, value: "", clicked: false },
      { id: 6, value: "", clicked: false },
      { id: 7, value: "", clicked: false },
      { id: 8, value: "", clicked: false },
      { id: 9, value: "", clicked: false }
    ],
    X_turn: true,
    win_poses: [
      [1, 2, 3],
      [4, 5, 6],
      [7, 8, 9],
      [1, 4, 7],
      [2, 5, 8],
      [3, 6, 9],
      [1, 5, 9],
      [3, 5, 7]
    ],
    X_pos: [],
    O_pos: []
  };
  handleClick = box => {
    if (!box.clicked) {
      let boxes = [...this.state.boxes];
      const index = boxes.indexOf(box);
      boxes[index].clicked = true;
      boxes[index].value = this.state.X_turn ? "X" : "O";
      let pos = box.id;
      let X_pos = [...this.state.X_pos];
      let O_pos = [...this.state.O_pos];
      if (this.state.X_turn) {
        X_pos.push(pos);
      } else {
        O_pos.push(pos);
      }
      this.setState({ boxes, X_turn: !this.state.X_turn, X_pos, O_pos });
    }
  };
  componentDidUpdate() {
    this.checkWin();
  }
  checkWin = () => {
    let gameOver = false;
    if (!this.state.X_turn) {
      gameOver = this.state.win_poses.some(v1 =>
        v1.every(v2 => this.state.X_pos.indexOf(v2) !== -1)
      );
    } else {
      gameOver = this.state.win_poses.some(v1 =>
        v1.every(v2 => this.state.O_pos.indexOf(v2) !== -1)
      );
    }
    if (gameOver) {
      let winner = this.state.X_turn ? "O" : "X";
      this.gameWon(winner);
    }
  };
  gameWon = winner => {
    console.log("Winner is ", winner);
  };
  render() {
    return (
      <div>
        <div className="game">
          {this.state.boxes.map(box => (
            <Box key={box.id} onClick={this.handleClick} box={box}>
              {box.value}
            </Box>
          ))}
        </div>
      </div>
    );
  }
}

export default Game;
