import React, { Component } from "react";
import Adder from "./components/adder";
import Tasks from "./components/tasks";

class App extends Component {
  state = {
    todos: []
  };
  handleChange = value => {
    let todos = JSON.parse(JSON.stringify(this.state.todos));
    let task = { id: todos.length + 1, task: value };
    todos.push(task);
    this.setState({ todos });
  };
  handleDelete = id => {
    let todos = JSON.parse(JSON.stringify(this.state.todos));
    let toDelIndex = todos.indexOf(todos.filter(task => task.id === id));
  };
  render() {
    return (
      <div className="App">
        <Adder onChange={this.handleChange} />
        <Tasks todos={this.state.todos} onDelete={this.handleDelete} />
      </div>
    );
  }
}

export default App;
