import React, { Component } from "react";

class Adder extends Component {
  render() {
    return (
      <div>
        <button
          onClick={() => this.props.onClick(this.refs.input.value)}
          className="todo-button"
        >
          +
        </button>
        <input
          className="todo-input"
          type="text"
          placeholder="Add A Task"
          autoFocus
          ref="input"
        />
      </div>
    );
  }
}

export default Adder;
