import { observable, decorate, autorun, computed } from "mobx";

class TodoStore {
  todos = ["Get Rekt", "Go To College", "Drown In Debt"];
  filter = "Wut";
  dummy = "NMS";
  get filteredTodos() {
    let MatchFilter = new RegExp(this.filter, "i");
    return this.todos.filter(todo => !this.filter || MatchFilter.test(todo));
  }
  createTodo(value) {
    this.todos.push(value);
  }
}

decorate(TodoStore, {
  todos: observable,
  filter: observable,
  filteredTodos: computed
});

let store = (window.store = new TodoStore());

export default store;

autorun(() => {
  console.log("Store Changed To: ", store.todos);
});
