const InitialUser = {
  name: "Aman",
  age: 17,
  gender: "MALE"
};
const InitialMovies = [
  {
    name: "FAF",
    rating: 0
  },
  {
    name: "FAF2",
    rating: 0
  },
  {
    name: "FAF3",
    rating: 0
  },
  {
    name: "FAF4",
    rating: 0
  },
  {
    name: "FAF5",
    rating: 2
  }
];

const UserReducer = (state = InitialUser, action) => {
  return state;
};
const MoviesReducer = (state = InitialMovies, action) => {
  return state;
};

const reducers = combineReducers({
  userInfo: UserReducer,
  MoviesWatched: MoviesReducer
});

const store = createStore(reducers);

store.subscribe(() => {
  console.log("Store Changed: ", store.getState());
});

store.dispatch({ type: "CHANGE_NAME", payload: "AMAN" });
store.dispatch({ type: "CHANGE_AGE", payload: 18 });
store.dispatch({ type: "CHANGE_GENDER", payload: "BISEXUAL" });
store.dispatch({ type: "RATE_MOVIE" });
