import { createStore } from "redux";

const reducer = (state, action) => {
  switch (action.type) {
    case "INC":
      return state + action.payload;
    case "DEC":
      return state - action.payload;
    case "MUL":
      return state * action.payload;
    case "DIV":
      return state / action.payload;
    case "POW":
      return state ** action.payload;
    default:
      return state;
  }
};

const store = createStore(reducer, 0);

store.subscribe(() => {
  console.log("Store Changed: ", store.getState());
});

store.dispatch({ type: "INC", payload: 1 });
