import React from "react";
import { connect } from "react-redux";

const mapStateToProps = state => {
  return {
    user: state.user.user
  };
};

const ConnectedApp = props => {
  return <h1>{this.props.user}</h1>;
};

const App = connect(mapStateToProps)(ConnectedApp);

export default App;
